  profilename: {
    fontSize: 15,
    fontWeight: '700',
    marginBottom: '2%',
  }, 
  divider: {
    height: 1,
    width: "100%",
    backgroundColor: "#3b3b3b",
    marginBottom: '2%',
  },
  button: {
    alignItems: 'center',
    justifyContent: 'center',
    height: 30,
    borderRadius: 5,
    fontSize: 15,
    marginBottom: 10,
  }, 
  buttonfilled: {
    alignItems: 'center',
    justifyContent: 'center',
    width: '80%',
    height: 40,
    borderRadius: '20',
    backgroundColor:'#DCDCDC',
    marginBottom: 20,
    boxShadow: 10,
  },
  textLeft: {
    flexDirection: "row", 
    alignItems: "center", 
    flex: 1,
    fontSize: 16,
    paddingLeft: 8,
  },
  displayBar: {
    flexDirection: 'row',
    paddingHorizontal: 12,
    paddingVertical: 10,
  },
  displayBox: {
    flex: 1/3,
    width: '100%',
    backgroundColor: 'white'
  },
  image: {
    width: 105,
    flexDirection: "row", 
    alignItems: "center", 
    marginLeft: '3%',
    marginRight: '3%',
    aspectRatio: 1/1,
  },
  row: {
    flexDirection: 'row',
    paddingVertical: 10,
    paddingHorizontal: 4
  }
});

const PICTURES = [
  {
    imageUrl: 'https://picsum.photos/id/1002/200',
  },
  {
    imageUrl: 'https://picsum.photos/id/1006/200',
  },
  {
    imageUrl: 'https://picsum.photos/id/1008/200',
  },
  {
    imageUrl: 'https://picsum.photos/id/1002/200',
  },
  {
    imageUrl: 'https://picsum.photos/id/1006/200',
  },
  {
    imageUrl: 'https://picsum.photos/id/1008/200',
  },
  {
    imageUrl: 'https://picsum.photos/id/1002/200',
  },
  {
    imageUrl: 'https://picsum.photos/id/1006/200',
  },
];

export default ProfilePage;
